<?/*Название:		debugwindowВход:			переменная, имя переменной, видимостьВыход:			вывод значения переменнойНазначение:		отладка*/function debugwindow($variable,$var_name = '',$visible=1){ if ($visible == 0)  echo '<!--'; echo  '<div align="center"><table border=1>  <tr>   <td align="center">'.$_SESSION['site_langcs']['core_debugwindow_title'].': <strong><u>'.$var_name.'</u></strong>   </td>  </tr>  <tr>   <td align="center">Просмотр переменных.   </td>  </tr>  <tr>   <td>'; switch(gettype($variable)) {  case 'object':   echo '   <textarea rows=10 cols=70>';   echo $_SESSION['site_langcs']['core_debugwindow_type']." - object\n";   echo $_SESSION['site_langcs']['core_debugwindow_attributes'].":\n";   print_r(get_object_vars($variable));   echo $_SESSION['site_langcs']['core_debugwindow_methods'].":\n";   print_r(get_class_methods($variable));   echo '</textarea></td></tr>';  break;  case 'integer':   echo $_SESSION['site_langcs']['core_debugwindow_type'].': <b>integer</b><br>'.$_SESSION['site_langcs']['core_debugwindow_value'].': '.$variable;  break;  case 'double':   echo $_SESSION['site_langcs']['core_debugwindow_type'].': <b>double</b><br>'.$_SESSION['site_langcs']['core_debugwindow_value'].': '.$variable;  break;  case 'string':   echo $_SESSION['site_langcs']['core_debugwindow_type'].': <b>string</b><br>'.$_SESSION['site_langcs']['core_debugwindow_value'].':<br><textarea rows=10 cols=70>'.$variable.'</textarea>';  break;  default:   echo '<textarea rows=20 cols=70>';   echo $_SESSION['site_langcs']['core_debugwindow_debugging'].".\n";   print_r($variable);   echo '</textarea>';  break; } echo '   </td>  </tr>  </table></div>'; if ($visible == 0)  echo '-->';}?>